参考实现 C++JavaPython <pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;

int n, q;

int main() {
    cin &gt;&gt; n &gt;&gt; q;
    for (int i = 1; i &lt;= q; i++) {
        int x;
        cin &gt;&gt; x;

        if (x == 1) {
            int k;
            cin &gt;&gt; k;
            cout &lt;&lt; ((n &gt;&gt; k) &amp; 1) &lt;&lt; endl;
        }

        if (x == 2) {
            int l, r;
            cin &gt;&gt; l &gt;&gt; r;
            int p = (1 &lt;&lt; (r + 1)) - 1;
            int t = (1 &lt;&lt; l) - 1;
            p -= t;
            cout &lt;&lt; (n ^ p) &lt;&lt; endl;
            n ^= p;
        }

        if (x == 3) {
            int l, r;
            cin &gt;&gt; l &gt;&gt; r;
            int p = (1 &lt;&lt; (r + 1)) - 1;
            int t = (1 &lt;&lt; l) - 1;
            p -= t;
            cout &lt;&lt; (n | p) &lt;&lt; endl;
            n |= p;
        }

        if (x == 4) {
            int l, r;
            cin &gt;&gt; l &gt;&gt; r;
            int p = (1 &lt;&lt; (r + 1)) - 1;
            int t = (1 &lt;&lt; l) - 1;
            p -= t;
            p = ~p;
            cout &lt;&lt; (n &amp; p) &lt;&lt; endl;
            n &amp;= p;
        }

        if (x == 5) {
            cout &lt;&lt; (n &amp; -n) &lt;&lt; endl;
        }
    }
    return 0;
}
