using namespace std;

bool check(int x) {
    int cnt = 1; // 从个位开始，第 1 位
    while (x) {
        int d = x % 10;
        if (cnt % 2 == 0) { // 偶数位
            if (d % 2 != 0) return false;
        } else { // 奇数位
            if (d % 2 == 0) return false;
        }
        x /= 10;
        cnt++;
    }
    return true;
}

int main() {
    int n, ans = 0;
    cin &gt;&gt; n;
    for (int i = 1; i &lt;= n; i++) {
        if (check(i)) ans++;
    }
    cout &lt;&lt; ans;
    return 0;
}
