参考实现 C++JavaPython <pre><code>// K 进制字符串转 10 进制
#include &lt;bits/stdc++.h&gt;
using namespace std;

int charToVal(char c) {
    if (isdigit(c)) return c - '0';
    return c - 'A' + 10;  // A~Z -&gt; 10~35
}

long long convertToDecimal(const string &amp;s, int base) {
    long long res = 0;
    for (char c : s) {
        res = res * base + charToVal(c);
    }
    return res;
}

int main() {
    string s;
    int base;
    cin &gt;&gt; s &gt;&gt; base;
    cout &lt;&lt; convertToDecimal(s, base) &lt;&lt; '\n';
    return 0;
}
</code></pre> <pre><code>import java.util.Scanner;

public class Main {

    // 字符转对应数值（0~9, A~Z）
    static int charToVal(char c) {
        if (c &gt;= '0' &amp;&amp; c &lt;= '9') return c - '0';
        return c - 'A' + 10;
    }

    // K 进制转 10 进制
    static long convertToDecimal(String s, int base) {
        long res = 0;
        for (int i = 0; i &lt; s.length(); i++) {
            res = res * base + charToVal(s.charAt(i));
        }
        return res;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int base = sc.nextInt();
        System.out.println(convertToDecimal(s, base));
    }
}
