参考实现 C++JavaPython <pre><code>// 10 进制转 K 进制（K &lt;= 36）
#include &lt;bits/stdc++.h&gt;
using namespace std;

string convertToBase(long long n, int base) {
    if (n == 0) return "0";
    string digits = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    string res;
    while (n &gt; 0) {
        res = digits[n % base] + res;
        n /= base;
    }
    return res;
}

int main() {
    long long n;
    int base;
    cin &gt;&gt; n &gt;&gt; base;
    cout &lt;&lt; convertToBase(n, base) &lt;&lt; '\n';
    return 0;
}
</code></pre> <pre><code>import java.util.Scanner;

public class Main {

    // 10 进制转 K 进制（K &lt;= 36）
    static String convertToBase(long n, int base) {
        if (n == 0) return "0";
        String digits = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        StringBuilder res = new StringBuilder();

        while (n &gt; 0) {
            res.append(digits.charAt((int)(n % base)));
            n /= base;
        }

        return res.reverse().toString();
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        int base = sc.nextInt();
        System.out.println(convertToBase(n, base));
    }
}
</code></pre> <pre><code># 10 进制转 K 进制（K &lt;= 36）

def convert_to_base(n, base):
    if n == 0:
        return "0"
    digits = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
    res = []
    while n &gt; 0:
        res.append(digits[n % base])
        n //= base
    return ''.join(reversed(res))

